select  count(*) from postHistory as ph,          posts as p,  		users as u,  		badges as b  where u.Id = p.OwnerUserId 	and p.OwnerUserId = ph.UserId 	and ph.UserId = b.UserId  AND p.PostTypeId=2  AND p.Score>=-2  AND p.Score<=60  AND p.FavoriteCount>=0  AND p.CreationDate<='2014-09-14 01:27:54'::timestamp  AND u.DownVotes<=0;
