select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.Score=1  AND c.CreationDate<='2014-09-11 07:27:31'::timestamp  AND p.ViewCount>=0  AND p.ViewCount<=6283  AND u.Views>=0  AND u.Views<=104  AND u.UpVotes<=2;
