select  count(*) from posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          badges as b,          users as u  where p.Id = pl.RelatedPostId 	and u.Id = p.OwnerUserId 	and u.Id = b.UserId 	and u.Id = ph.UserId     and u.Id = v.UserId  AND ph.PostHistoryTypeId=5  AND p.PostTypeId=2  AND p.Score>=-1  AND p.FavoriteCount>=0  AND p.FavoriteCount<=15  AND u.Reputation>=1  AND u.Reputation<=140  AND u.UpVotes>=0  AND u.CreationDate<='2014-09-09 23:32:58'::timestamp;
