select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.Score=0  AND ph.PostHistoryTypeId=2  AND pl.LinkTypeId=1  AND p.Score>=0  AND p.Score<=17  AND p.AnswerCount>=0  AND p.AnswerCount<=8  AND p.CreationDate>='2010-07-20 08:49:13'::timestamp  AND v.VoteTypeId=2;
