select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND c.Score=1  AND c.CreationDate>='2010-09-04 15:32:22'::timestamp  AND c.CreationDate<='2014-09-01 06:21:53'::timestamp  AND p.PostTypeId=1  AND p.Score>=-5  AND p.AnswerCount<=7  AND p.FavoriteCount>=0  AND v.VoteTypeId=2  AND v.CreationDate<='2014-09-13 00:00:00'::timestamp;
