select  count(*) from comments as c,          postLinks as pl,          posts as p,  		users as u,  		badges as b   where p.Id = pl.RelatedPostId 	and p.Id = c.PostId 	and u.Id = b.UserId 	and u.Id = p.OwnerUserId  AND b.Date<='2014-09-03 10:03:20'::timestamp  AND pl.LinkTypeId=1  AND pl.CreationDate>='2011-01-22 19:55:55'::timestamp  AND pl.CreationDate<='2014-05-10 11:16:37'::timestamp  AND p.ViewCount>=0  AND p.ViewCount<=7663  AND p.CommentCount<=8  AND u.Views>=0  AND u.UpVotes>=0;
