select  count(*) from comments as c,  		posts as p,          postLinks as pl,          votes as v,          badges as b,          users as u  where p.Id = c.PostId 	and p.Id = pl.RelatedPostId     and p.Id = v.PostId  	and u.Id = p.LastEditorUserId     and u.Id = b.UserId  AND c.CreationDate<='2014-09-04 15:22:45'::timestamp  AND pl.LinkTypeId=1  AND pl.CreationDate<='2014-09-07 21:11:00'::timestamp  AND u.Views<=37;
