select  count(*) from comments as c,          postLinks as pl,          posts as p,  		users as u,  		badges as b   where p.Id = pl.RelatedPostId 	and p.Id = c.PostId 	and u.Id = b.UserId 	and u.Id = p.OwnerUserId  AND pl.LinkTypeId=1  AND pl.CreationDate<='2014-08-14 11:26:15'::timestamp  AND p.PostTypeId=1  AND p.ViewCount>=0  AND p.AnswerCount>=0  AND p.AnswerCount<=8  AND p.FavoriteCount>=0  AND p.FavoriteCount<=8  AND u.CreationDate>='2010-07-19 19:18:14'::timestamp;
