select  count(*) from comments as c,  		postHistory as ph,  		badges as b,          votes as v,          users as u  where u.Id =b.UserId 	and b.UserId = ph.UserId 	and ph.UserId = v.UserId 	and v.UserId = c.UserId  AND b.Date<='2014-09-11 20:15:50'::timestamp  AND c.Score=1  AND ph.PostHistoryTypeId=5  AND ph.CreationDate<='2014-08-17 22:57:13'::timestamp  AND u.Views>=0  AND u.Views<=260  AND u.UpVotes=0  AND v.VoteTypeId=2;
