select  count(*) from comments as c,  		posts as p,           postHistory as ph,  		badges as b,          users as u  where u.Id = c.UserId 	and u.Id = p.LastEditorUserId 	and u.Id = ph.UserId 	and u.Id = b.UserId  AND c.Score=0  AND ph.PostHistoryTypeId=2  AND p.PostTypeId=2  AND p.ViewCount>=0  AND p.ViewCount<=2291  AND p.FavoriteCount>=0  AND p.FavoriteCount<=7  AND u.UpVotes<=17;
